import { ref } from 'vue';
import { Controller } from 'swiper';
import { Swiper, SwiperSlide } from 'swiper/vue';
export default {
  components: {
    Swiper,
    SwiperSlide
  },
  setup() {
    const controlledSwiper = ref(null);
    const setControlledSwiper = swiper => {
      controlledSwiper.value = swiper;
    };
    return {
      Controller,
      controlledSwiper,
      setControlledSwiper
    };
  }
};
