import Home from '../components/views/Home';

//Vue와 VueRouter 연결
Vue.use(VueRouter);

//우리가 사용할 route 생성 및 설정
const routes = [
  {
    path: '/',
    name: 'Home',
    component: Home,
  },
];

//VueRouter에 route를 등록하고 설정한다.
const router = createRouter({
    history: createWebHistory(),
    routes
})

//설정한 VueRouter 내보낸다.
export default router;
